import React, { useRef, useState } from "react";
import html2canvas from "html2canvas";
import "./App.css";

const defaultCaption = "‡¶ó‡¶®‡¶Æ‡¶æ‡¶ß‡ßç‡¶Ø‡¶Æ‡ßá‡¶∞ ‡¶Ö‡¶™‡¶∏‡¶æ‡¶Ç‡¶¨‡¶æ‡¶¶‡¶ø‡¶ï‡¶§‡¶æ‡¶∞ ‡¶™‡¶ï‡ßç‡¶∑‡ßá ‡¶¨‡¶ø‡¶¨‡ßÉ‡¶§‡¶ø ‡¶¶‡¶ø‡¶≤‡ßá‡¶® ‡¶è‡¶ï‡¶ù‡¶æ‡¶Å‡¶ï ‡¶π‡¶≤‡ßÅ‡¶¶ ‡¶∏‡¶æ‡¶Ç‡¶¨‡¶æ‡¶¶‡¶ø‡¶ï";
const defaultDate = "‡ß¶‡ßÆ ‡¶ú‡ßÅ‡¶≤‡¶æ‡¶á ‡ß®‡ß¶‡ß®‡ß´";
const defaultCredit = "‡¶õ‡¶¨‡¶ø: ‡¶™‡ßç‡¶∞‡¶•‡¶Æ ‡¶Ü‡¶≤‡ßã";
const TEMPLATE_URL = "https://fotocard.vercel.app/assets/bk-template.png";
const TEMPLATE_SIZE = 1000;
const BOX_X = 16; // px
const BOX_Y = 32; // px
const BOX_WIDTH = 960; // px
const BOX_HEIGHT = 670; // px

function App() {
  const [image, setImage] = useState(null);
  const [caption, setCaption] = useState(defaultCaption);
  const [date, setDate] = useState(defaultDate);
  const [credit, setCredit] = useState(defaultCredit);
  const [error, setError] = useState("");
  const fileInput = useRef();
  const cardRef = useRef();

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const validTypes = ["image/jpeg", "image/png", "image/gif", "image/bmp", "image/webp"];
    if (!validTypes.includes(file.type)) {
      setError("‡¶∂‡ßÅ‡¶ß‡ßÅ‡¶Æ‡¶æ‡¶§‡ßç‡¶∞ JPG, PNG, GIF, BMP, WebP ‡¶´‡¶∞‡¶Æ‡ßç‡¶Ø‡¶æ‡¶ü ‡¶∏‡¶æ‡¶™‡ßã‡¶∞‡ßç‡¶ü ‡¶ï‡¶∞‡¶¨‡ßá");
      return;
    }
    setError("");
    const reader = new FileReader();
    reader.onload = (ev) => setImage(ev.target.result);
    reader.readAsDataURL(file);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      fileInput.current.files = e.dataTransfer.files;
      handleImageChange({ target: { files: e.dataTransfer.files } });
    }
  };

  const handleDownload = async () => {
    if (!image) return;
    const node = cardRef.current;
    const scale = TEMPLATE_SIZE / node.offsetWidth;
    const canvas = await html2canvas(node, {
      useCORS: true,
      backgroundColor: null,
      width: TEMPLATE_SIZE,
      height: TEMPLATE_SIZE,
      scale: scale,
      windowWidth: TEMPLATE_SIZE,
      windowHeight: TEMPLATE_SIZE
    });
    const link = document.createElement("a");
    link.download = "fotocard.png";
    link.href = canvas.toDataURL();
    link.click();
  };

  const handleClear = () => {
    setImage(null);
    setError("");
    fileInput.current.value = null;
  };

  return (
    <div className="container">
      <h1 className="title">‡¶¨‡¶æ‡¶≤‡ßá‡¶∞‡¶ï‡¶£‡ßç‡¶† ‡¶´‡¶ü‡ßã‡¶ï‡¶æ‡¶∞‡ßç‡¶° <span className="yellow">‡¶π‡¶≤‡ßÅ‡¶¶ ‡¶Ü‡¶≤‡ßã ‡¶´‡¶ü‡ßã‡¶ï‡¶æ‡¶∞‡ßç‡¶°</span></h1>
      <div className="upload-section">
        <label htmlFor="file-upload" className="upload-label" onDrop={handleDrop} onDragOver={e => e.preventDefault()}>
          {image ? (
            <img src={image} alt="Uploaded" className="preview-img" />
          ) : (
            <div className="upload-placeholder">
              <span role="img" aria-label="camera" className="camera-icon">üì∑</span>
              <div>‡¶â‡¶™‡¶∞‡ßá‡¶∞ ‡¶®‡ßÄ‡¶≤ ‡¶¨‡¶æ‡¶ü‡¶®‡ßá ‡¶ï‡ßç‡¶≤‡¶ø‡¶ï ‡¶ï‡¶∞‡ßá ‡¶Ö‡¶•‡¶¨‡¶æ ‡¶è‡¶ñ‡¶æ‡¶®‡ßá ‡¶õ‡¶¨‡¶ø ‡¶è‡¶®‡ßá ‡¶õ‡ßá‡ßú‡ßá ‡¶¶‡¶ø‡¶®</div>
              <div className="formats">JPG, PNG, GIF, BMP, WebP ‡¶´‡¶∞‡¶Æ‡ßç‡¶Ø‡¶æ‡¶ü‡ßá‡¶∞ ‡¶õ‡¶¨‡¶ø‡¶ó‡ßÅ‡¶≤‡¶æ ‡¶∏‡¶æ‡¶™‡ßã‡¶∞‡ßç‡¶ü ‡¶ï‡¶∞‡¶¨‡ßá</div>
            </div>
          )}
          <input
            id="file-upload"
            type="file"
            accept="image/jpeg,image/png,image/gif,image/bmp,image/webp"
            onChange={handleImageChange}
            ref={fileInput}
            style={{ display: "none" }}
          />
        </label>
        <div className="button-row">
          <button className="blue-btn" onClick={() => fileInput.current.click()}>‡¶õ‡¶¨‡¶ø ‡¶¨‡ßá‡¶õ‡ßá ‡¶®‡¶ø‡¶®</button>
          <button className="clear-btn" onClick={handleClear}>‡¶ï‡ßç‡¶≤‡¶ø‡ßü‡¶æ‡¶∞ ‡¶ï‡¶∞‡ßÅ‡¶®</button>
        </div>
        {error && <div className="error">{error}</div>}
      </div>
      <div className="editor-section">
        <input
          className="caption-input"
          type="text"
          value={caption}
          onChange={e => setCaption(e.target.value)}
          placeholder="‡¶ï‡ßç‡¶Ø‡¶æ‡¶™‡¶∂‡¶®"
        />
        <input
          className="date-input"
          type="text"
          value={date}
          onChange={e => setDate(e.target.value)}
          placeholder="‡¶§‡¶æ‡¶∞‡¶ø‡¶ñ"
        />
      </div>
      <div className="card-preview-wrapper">
        <div
          className="card-preview"
          ref={cardRef}
          style={{
            fontFamily: "'Noto Sans Bengali', sans-serif",
            width: TEMPLATE_SIZE,
            height: TEMPLATE_SIZE,
            position: 'relative',
            background: `url(${TEMPLATE_URL}) 0 0/100% 100% no-repeat`,
            margin: '0 auto',
            display: 'block',
          }}
        >
          {/* Uploaded image inside the white box */}
          {image && (
            <img
              src={image}
              alt="card"
              style={{
                position: 'absolute',
                left: BOX_X,
                top: BOX_Y,
                width: BOX_WIDTH,
                height: BOX_HEIGHT,
                objectFit: 'contain',
                borderRadius: '24px',
                background: 'none',
              }}
            />
          )}
          {/* Text below the box, centered */}
          <div
            style={{
              position: 'absolute',
              top: BOX_Y + BOX_HEIGHT + 24, // 24px gap below box
              left: 0,
              width: '100%',
              textAlign: 'center',
              padding: '0 32px',
              fontFamily: "'Noto Sans Bengali', sans-serif",
            }}
          >
            <div className="caption" style={{ fontSize: '2.1rem', fontWeight: 700, marginBottom: 8, lineHeight: 1.2 }}>{caption}</div>
            <div className="date" style={{ fontSize: '1.2rem', fontWeight: 500, marginBottom: 4 }}>{date}</div>
            <div className="credit" style={{ fontSize: '1.1rem', color: '#444', fontWeight: 400 }}>{credit}</div>
          </div>
        </div>
      </div>
      {/* Text under the template image, outside 1000x1000 area */}
      <div style={{
        width: TEMPLATE_SIZE,
        maxWidth: '100vw',
        margin: '0 auto',
        marginTop: 16,
        textAlign: 'left',
        fontFamily: "'Noto Sans Bengali', sans-serif"
      }}>
      
      </div>
      <button className="download-btn" onClick={handleDownload} disabled={!image}>‡¶°‡¶æ‡¶â‡¶®‡¶≤‡ßã‡¶° ‡¶ï‡¶∞‡ßÅ‡¶®</button>
      <div className="footer">‡¶è‡¶Ü‡¶á ‡¶¶‡¶ø‡ßü‡ßá ‡¶®‡¶ø‡¶∞‡ßç‡¶Æ‡¶ø‡¶§‡•§ ‡¶Ü‡¶Æ‡¶æ‡¶∞ ‡¶ï‡ßã‡¶® ‡¶¶‡ßã‡¶∑ ‡¶®‡¶æ‡¶á‡•§</div>
    </div>
  );
}

export default App;
