import React, { useRef, useState } from "react";
import html2canvas from "html2canvas";
import "./App.css";
import templateImg from './assets/template.png';

const defaultCaption = "গনমাধ্যমের অপসাংবাদিকতার পক্ষে বিবৃতি দিলেন একঝাঁক হলুদ সাংবাদিক";
const defaultDate = "০৮ জুলাই ২০২৫";
const defaultCredit = "ছবি: প্রথম আলো";
const TEMPLATE_SIZE = 1000;
const BOX_X = 16; // px
const BOX_Y = 32; // px
const BOX_WIDTH = 960; // px
const BOX_HEIGHT = 670; // px
const BOX_RADIUS = 24; // px
const TEXT_AREA_X = BOX_X;
const TEXT_AREA_Y = BOX_Y + BOX_HEIGHT + 32;
const TEXT_AREA_WIDTH = 960;

// Helper to wrap text for canvas
function wrapText(ctx, text, x, y, maxWidth, lineHeight) {
  const words = text.split(' ');
  let line = '';
  let lines = [];
  for (let n = 0; n < words.length; n++) {
    const testLine = line + (line ? ' ' : '') + words[n];
    const metrics = ctx.measureText(testLine);
    if (metrics.width > maxWidth && n > 0) {
      lines.push(line);
      line = words[n];
    } else {
      line = testLine;
    }
  }
  lines.push(line);
  for (let i = 0; i < lines.length; i++) {
    ctx.fillText(lines[i], x, y + i * lineHeight);
  }
  return lines.length;
}

function App() {
  const [image, setImage] = useState(null);
  const [caption, setCaption] = useState(defaultCaption);
  const [date, setDate] = useState(defaultDate);
  const [credit, setCredit] = useState(defaultCredit);
  const [error, setError] = useState("");
  const fileInput = useRef();
  const cardRef = useRef();

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const validTypes = ["image/jpeg", "image/png", "image/gif", "image/bmp", "image/webp"];
    if (!validTypes.includes(file.type)) {
      setError("শুধুমাত্র JPG, PNG, GIF, BMP, WebP ফরম্যাট সাপোর্ট করবে");
      return;
    }
    setError("");
    const reader = new FileReader();
    reader.onload = (ev) => setImage(ev.target.result);
    reader.readAsDataURL(file);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      fileInput.current.files = e.dataTransfer.files;
      handleImageChange({ target: { files: e.dataTransfer.files } });
    }
  };

  const handleDownload = async () => {
    if (!image) return;
    // Create a canvas and draw the template, uploaded image, and text
    const canvas = document.createElement('canvas');
    canvas.width = TEMPLATE_SIZE;
    canvas.height = TEMPLATE_SIZE;
    const ctx = canvas.getContext('2d');

    // Draw the template image as background
    const bg = new window.Image();
    bg.src = templateImg;
    await new Promise(resolve => { bg.onload = resolve; });
    ctx.drawImage(bg, 0, 0, TEMPLATE_SIZE, TEMPLATE_SIZE);

    // Draw the uploaded image inside the box, maintaining aspect ratio (object-fit: contain)
    const userImg = new window.Image();
    userImg.src = image;
    await new Promise(resolve => { userImg.onload = resolve; });
    // Calculate object-fit: contain placement
    const boxAR = BOX_WIDTH / BOX_HEIGHT;
    const imgAR = userImg.width / userImg.height;
    let drawW = BOX_WIDTH, drawH = BOX_HEIGHT, drawX = BOX_X, drawY = BOX_Y;
    if (imgAR > boxAR) {
      // Image is wider
      drawW = BOX_WIDTH;
      drawH = BOX_WIDTH / imgAR;
      drawY = BOX_Y + (BOX_HEIGHT - drawH) / 2;
    } else {
      // Image is taller
      drawH = BOX_HEIGHT;
      drawW = BOX_HEIGHT * imgAR;
      drawX = BOX_X + (BOX_WIDTH - drawW) / 2;
    }
    ctx.save();
    ctx.beginPath();
    ctx.moveTo(BOX_X + BOX_RADIUS, BOX_Y);
    ctx.lineTo(BOX_X + BOX_WIDTH - BOX_RADIUS, BOX_Y);
    ctx.quadraticCurveTo(BOX_X + BOX_WIDTH, BOX_Y, BOX_X + BOX_WIDTH, BOX_Y + BOX_RADIUS);
    ctx.lineTo(BOX_X + BOX_WIDTH, BOX_Y + BOX_HEIGHT - BOX_RADIUS);
    ctx.quadraticCurveTo(BOX_X + BOX_WIDTH, BOX_Y + BOX_HEIGHT, BOX_X + BOX_WIDTH - BOX_RADIUS, BOX_Y + BOX_HEIGHT);
    ctx.lineTo(BOX_X + BOX_RADIUS, BOX_Y + BOX_HEIGHT);
    ctx.quadraticCurveTo(BOX_X, BOX_Y + BOX_HEIGHT, BOX_X, BOX_Y + BOX_HEIGHT - BOX_RADIUS);
    ctx.lineTo(BOX_X, BOX_Y + BOX_RADIUS);
    ctx.quadraticCurveTo(BOX_X, BOX_Y, BOX_X + BOX_RADIUS, BOX_Y);
    ctx.closePath();
    ctx.clip();
    ctx.drawImage(userImg, drawX, drawY, drawW, drawH);
    ctx.restore();

    // Draw the text below the box, centered and wrapped
    ctx.textAlign = 'center';
    ctx.textBaseline = 'top';
    let textY = TEXT_AREA_Y;
    // Caption
    ctx.font = 'bold 38px Noto Sans Bengali, sans-serif';
    ctx.fillStyle = '#222';
    const capLines = wrapText(ctx, caption, TEMPLATE_SIZE / 2, textY, TEXT_AREA_WIDTH - 40, 44);
    textY += capLines * 44 + 8;
    // Date
    ctx.font = '500 28px Noto Sans Bengali, sans-serif';
    ctx.fillStyle = '#222';
    const dateLines = wrapText(ctx, date, TEMPLATE_SIZE / 2, textY, TEXT_AREA_WIDTH - 40, 34);
    textY += dateLines * 34 + 4;
    // Credit
    ctx.font = '400 24px Noto Sans Bengali, sans-serif';
    ctx.fillStyle = '#444';
    wrapText(ctx, credit, TEMPLATE_SIZE / 2, textY, TEXT_AREA_WIDTH - 40, 30);

    // Download
    const link = document.createElement('a');
    link.download = 'fotocard.png';
    link.href = canvas.toDataURL();
    link.click();
  };

  const handleClear = () => {
    setImage(null);
    setError("");
    fileInput.current.value = null;
  };

  return (
    <div className="container">
      <h1 className="title">বালেরকণ্ঠ ফটোকার্ড <span className="yellow">হলুদ আলো ফটোকার্ড</span></h1>
      <div className="upload-section">
        <label htmlFor="file-upload" className="upload-label" onDrop={handleDrop} onDragOver={e => e.preventDefault()}>
          {image ? (
            <img src={image} alt="Uploaded" className="preview-img" />
          ) : (
            <div className="upload-placeholder">
              <span role="img" aria-label="camera" className="camera-icon">📷</span>
              <div>উপরের নীল বাটনে ক্লিক করে অথবা এখানে ছবি এনে ছেড়ে দিন</div>
              <div className="formats">JPG, PNG, GIF, BMP, WebP ফরম্যাটের ছবিগুলা সাপোর্ট করবে</div>
            </div>
          )}
          <input
            id="file-upload"
            type="file"
            accept="image/jpeg,image/png,image/gif,image/bmp,image/webp"
            onChange={handleImageChange}
            ref={fileInput}
            style={{ display: "none" }}
          />
        </label>
        <div className="button-row">
          <button className="blue-btn" onClick={() => fileInput.current.click()}>ছবি বেছে নিন</button>
          <button className="clear-btn" onClick={handleClear}>ক্লিয়ার করুন</button>
        </div>
        {error && <div className="error">{error}</div>}
      </div>
      <div className="editor-section">
        <input
          className="caption-input"
          type="text"
          value={caption}
          onChange={e => setCaption(e.target.value)}
          placeholder="ক্যাপশন"
        />
        <input
          className="date-input"
          type="text"
          value={date}
          onChange={e => setDate(e.target.value)}
          placeholder="তারিখ"
        />
      </div>
      <div className="card-preview-wrapper">
        <div
          className="card-preview"
          ref={cardRef}
          style={{
            fontFamily: "'Noto Sans Bengali', sans-serif",
            width: TEMPLATE_SIZE,
            height: TEMPLATE_SIZE,
            position: 'relative',
            background: `url(${templateImg}) 0 0/100% 100% no-repeat`,
            margin: '0 auto',
            display: 'block',
          }}
        >
          {/* Uploaded image inside the white box */}
          {image && (
            <img
              src={image}
              alt="card"
              style={{
                position: 'absolute',
                left: BOX_X,
                top: BOX_Y,
                width: BOX_WIDTH,
                height: BOX_HEIGHT,
                objectFit: 'contain',
                borderRadius: `${BOX_RADIUS}px`,
                background: 'none',
              }}
            />
          )}
          {/* Text below the box, centered and wrapped */}
          <div
            style={{
              position: 'absolute',
              top: TEXT_AREA_Y,
              left: TEXT_AREA_X,
              width: TEXT_AREA_WIDTH,
              textAlign: 'center',
              padding: '0 20px',
              fontFamily: "'Noto Sans Bengali', sans-serif",
              color: '#222',
            }}
          >
            <div className="caption" style={{ fontSize: '2.1rem', fontWeight: 700, marginBottom: 8, lineHeight: 1.2, wordBreak: 'break-word' }}>{caption}</div>
            <div className="date" style={{ fontSize: '1.2rem', fontWeight: 500, marginBottom: 4, wordBreak: 'break-word' }}>{date}</div>
            <div className="credit" style={{ fontSize: '1.1rem', color: '#444', fontWeight: 400, wordBreak: 'break-word' }}>{credit}</div>
          </div>
        </div>
      </div>
      {/* Text under the template image, outside 1000x1000 area */}
      <div style={{
        width: TEMPLATE_SIZE,
        maxWidth: '100vw',
        margin: '0 auto',
        marginTop: 16,
        textAlign: 'left',
        fontFamily: "'Noto Sans Bengali', sans-serif"
      }}>
      
      </div>
      <button className="download-btn" onClick={handleDownload} disabled={!image}>ডাউনলোড করুন</button>
      <div className="footer">তৈরী করেছে: <a href="https://www.facebook.com/smtir">তাওহিদুল ইসলাম রাজীব</a></div>
    </div>
  );
}

export default App;
