import React, { useRef, useState } from "react";
import html2canvas from "html2canvas";
import "./App.css";

const defaultCaption = "গনমাধ্যমের অপসাংবাদিকতার পক্ষে বিবৃতি দিলেন একঝাঁক হলুদ সাংবাদিক";
const defaultDate = "০৮ জুলাই ২০২৫";
const defaultCredit = "ছবি: প্রথম আলো";

function App() {
  const [image, setImage] = useState(null);
  const [caption, setCaption] = useState(defaultCaption);
  const [date, setDate] = useState(defaultDate);
  const [credit, setCredit] = useState(defaultCredit);
  const [error, setError] = useState("");
  const fileInput = useRef();
  const cardRef = useRef();

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const validTypes = ["image/jpeg", "image/png", "image/gif", "image/bmp", "image/webp"];
    if (!validTypes.includes(file.type)) {
      setError("শুধুমাত্র JPG, PNG, GIF, BMP, WebP ফরম্যাট সাপোর্ট করবে");
      return;
    }
    setError("");
    const reader = new FileReader();
    reader.onload = (ev) => setImage(ev.target.result);
    reader.readAsDataURL(file);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      fileInput.current.files = e.dataTransfer.files;
      handleImageChange({ target: { files: e.dataTransfer.files } });
    }
  };

  const handleDownload = async () => {
    if (!image) return;
    // Render at 1200x630 for high resolution
    const node = cardRef.current;
    const scale = 1200 / node.offsetWidth;
    const canvas = await html2canvas(node, {
      useCORS: true,
      backgroundColor: null,
      width: node.offsetWidth,
      height: node.offsetHeight,
      scale: scale,
      windowWidth: node.offsetWidth,
      windowHeight: node.offsetHeight
    });
    const link = document.createElement("a");
    link.download = "fotocard.png";
    link.href = canvas.toDataURL();
    link.click();
  };

  const handleClear = () => {
    setImage(null);
    setError("");
    fileInput.current.value = null;
  };

  return (
    <div className="container">
      <h1 className="title">বালেরকণ্ঠ ফটোকার্ড <span className="yellow">হলুদ আলো ফটোকার্ড</span></h1>
      <div className="upload-section">
        <label htmlFor="file-upload" className="upload-label" onDrop={handleDrop} onDragOver={e => e.preventDefault()}>
          {image ? (
            <img src={image} alt="Uploaded" className="preview-img" />
          ) : (
            <div className="upload-placeholder">
              <span role="img" aria-label="camera" className="camera-icon">📷</span>
              <div>উপরের নীল বাটনে ক্লিক করে অথবা এখানে ছবি এনে ছেড়ে দিন</div>
              <div className="formats">JPG, PNG, GIF, BMP, WebP ফরম্যাটের ছবিগুলা সাপোর্ট করবে</div>
            </div>
          )}
          <input
            id="file-upload"
            type="file"
            accept="image/jpeg,image/png,image/gif,image/bmp,image/webp"
            onChange={handleImageChange}
            ref={fileInput}
            style={{ display: "none" }}
          />
        </label>
        <div className="button-row">
          <button className="blue-btn" onClick={() => fileInput.current.click()}>ছবি বেছে নিন</button>
          <button className="clear-btn" onClick={handleClear}>ক্লিয়ার করুন</button>
        </div>
        {error && <div className="error">{error}</div>}
      </div>
      <div className="editor-section">
        <input
          className="caption-input"
          type="text"
          value={caption}
          onChange={e => setCaption(e.target.value)}
          placeholder="ক্যাপশন"
        />
        <input
          className="date-input"
          type="text"
          value={date}
          onChange={e => setDate(e.target.value)}
          placeholder="তারিখ"
        />
      </div>
      <div className="card-preview-wrapper">
        <div className="card-preview" ref={cardRef} style={{ fontFamily: "'Noto Sans Bengali', sans-serif", aspectRatio: "1200/630" }}>
          {image ? (
            <img src={image} alt="card" className="card-img" />
          ) : (
            <div className="card-img card-img-placeholder"></div>
          )}
          <div className="overlay">
            <div className="caption">{caption}</div>
            <div className="date">{date}</div>
            <div className="credit">{credit}</div>
          </div>
        </div>
      </div>
      <button className="download-btn" onClick={handleDownload} disabled={!image}>ডাউনলোড করুন</button>
      <div className="footer">এআই দিয়ে নির্মিত। আমার কোন দোষ নাই।</div>
    </div>
  );
}

export default App;
