import React, { useRef, useState } from "react";
import html2canvas from "html2canvas";
import "./App.css";
import templateImg from './assets/bk-template.png';

const defaultCaption = "‡¶ó‡¶®‡¶Æ‡¶æ‡¶ß‡ßç‡¶Ø‡¶Æ‡ßá‡¶∞ ‡¶Ö‡¶™‡¶∏‡¶æ‡¶Ç‡¶¨‡¶æ‡¶¶‡¶ø‡¶ï‡¶§‡¶æ‡¶∞ ‡¶™‡¶ï‡ßç‡¶∑‡ßá ‡¶¨‡¶ø‡¶¨‡ßÉ‡¶§‡¶ø ‡¶¶‡¶ø‡¶≤‡ßá‡¶® ‡¶è‡¶ï‡¶ù‡¶æ‡¶Å‡¶ï ‡¶π‡¶≤‡ßÅ‡¶¶ ‡¶∏‡¶æ‡¶Ç‡¶¨‡¶æ‡¶¶‡¶ø‡¶ï";
const defaultDate = "‡ß¶‡ßÆ ‡¶ú‡ßÅ‡¶≤‡¶æ‡¶á ‡ß®‡ß¶‡ß®‡ß´";
const defaultCredit = "‡¶õ‡¶¨‡¶ø: ‡¶™‡ßç‡¶∞‡¶•‡¶Æ ‡¶Ü‡¶≤‡ßã";
const TEMPLATE_SIZE = 1000;
const BOX_X = 16; // px
const BOX_Y = 32; // px
const BOX_WIDTH = 960; // px
const BOX_HEIGHT = 670; // px

function App() {
  const [image, setImage] = useState(null);
  const [caption, setCaption] = useState(defaultCaption);
  const [date, setDate] = useState(defaultDate);
  const [credit, setCredit] = useState(defaultCredit);
  const [error, setError] = useState("");
  const fileInput = useRef();
  const cardRef = useRef();

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const validTypes = ["image/jpeg", "image/png", "image/gif", "image/bmp", "image/webp"];
    if (!validTypes.includes(file.type)) {
      setError("‡¶∂‡ßÅ‡¶ß‡ßÅ‡¶Æ‡¶æ‡¶§‡ßç‡¶∞ JPG, PNG, GIF, BMP, WebP ‡¶´‡¶∞‡¶Æ‡ßç‡¶Ø‡¶æ‡¶ü ‡¶∏‡¶æ‡¶™‡ßã‡¶∞‡ßç‡¶ü ‡¶ï‡¶∞‡¶¨‡ßá");
      return;
    }
    setError("");
    const reader = new FileReader();
    reader.onload = (ev) => setImage(ev.target.result);
    reader.readAsDataURL(file);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      fileInput.current.files = e.dataTransfer.files;
      handleImageChange({ target: { files: e.dataTransfer.files } });
    }
  };

  const handleDownload = async () => {
    if (!image) return;
    // Create a canvas and draw the template, uploaded image, and text
    const canvas = document.createElement('canvas');
    canvas.width = TEMPLATE_SIZE;
    canvas.height = TEMPLATE_SIZE;
    const ctx = canvas.getContext('2d');

    // Draw the template image as background
    const bg = new window.Image();
    bg.src = templateImg;
    await new Promise(resolve => { bg.onload = resolve; });
    ctx.drawImage(bg, 0, 0, TEMPLATE_SIZE, TEMPLATE_SIZE);

    // Draw the uploaded image inside the box, maintaining aspect ratio (object-fit: contain)
    const userImg = new window.Image();
    userImg.src = image;
    await new Promise(resolve => { userImg.onload = resolve; });
    // Calculate object-fit: contain placement
    const boxAR = BOX_WIDTH / BOX_HEIGHT;
    const imgAR = userImg.width / userImg.height;
    let drawW = BOX_WIDTH, drawH = BOX_HEIGHT, drawX = BOX_X, drawY = BOX_Y;
    if (imgAR > boxAR) {
      // Image is wider
      drawW = BOX_WIDTH;
      drawH = BOX_WIDTH / imgAR;
      drawY = BOX_Y + (BOX_HEIGHT - drawH) / 2;
    } else {
      // Image is taller
      drawH = BOX_HEIGHT;
      drawW = BOX_HEIGHT * imgAR;
      drawX = BOX_X + (BOX_WIDTH - drawW) / 2;
    }
    ctx.save();
    ctx.beginPath();
    ctx.moveTo(BOX_X + 24, BOX_Y);
    ctx.lineTo(BOX_X + BOX_WIDTH - 24, BOX_Y);
    ctx.quadraticCurveTo(BOX_X + BOX_WIDTH, BOX_Y, BOX_X + BOX_WIDTH, BOX_Y + 24);
    ctx.lineTo(BOX_X + BOX_WIDTH, BOX_Y + BOX_HEIGHT - 24);
    ctx.quadraticCurveTo(BOX_X + BOX_WIDTH, BOX_Y + BOX_HEIGHT, BOX_X + BOX_WIDTH - 24, BOX_Y + BOX_HEIGHT);
    ctx.lineTo(BOX_X + 24, BOX_Y + BOX_HEIGHT);
    ctx.quadraticCurveTo(BOX_X, BOX_Y + BOX_HEIGHT, BOX_X, BOX_Y + BOX_HEIGHT - 24);
    ctx.lineTo(BOX_X, BOX_Y + 24);
    ctx.quadraticCurveTo(BOX_X, BOX_Y, BOX_X + 24, BOX_Y);
    ctx.closePath();
    ctx.clip();
    ctx.drawImage(userImg, drawX, drawY, drawW, drawH);
    ctx.restore();

    // Draw the text below the box, centered
    ctx.font = 'bold 38px Noto Sans Bengali, sans-serif';
    ctx.fillStyle = '#222';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'top';
    let textY = BOX_Y + BOX_HEIGHT + 32;
    ctx.fillText(caption, TEMPLATE_SIZE / 2, textY);
    ctx.font = '500 28px Noto Sans Bengali, sans-serif';
    textY += 48;
    ctx.fillText(date, TEMPLATE_SIZE / 2, textY);
    ctx.font = '400 24px Noto Sans Bengali, sans-serif';
    textY += 36;
    ctx.fillStyle = '#444';
    ctx.fillText(credit, TEMPLATE_SIZE / 2, textY);

    // Download
    const link = document.createElement('a');
    link.download = 'fotocard.png';
    link.href = canvas.toDataURL();
    link.click();
  };

  const handleClear = () => {
    setImage(null);
    setError("");
    fileInput.current.value = null;
  };

  return (
    <div className="container">
      <h1 className="title">‡¶¨‡¶æ‡¶≤‡ßá‡¶∞‡¶ï‡¶£‡ßç‡¶† ‡¶´‡¶ü‡ßã‡¶ï‡¶æ‡¶∞‡ßç‡¶° <span className="yellow">‡¶π‡¶≤‡ßÅ‡¶¶ ‡¶Ü‡¶≤‡ßã ‡¶´‡¶ü‡ßã‡¶ï‡¶æ‡¶∞‡ßç‡¶°</span></h1>
      <div className="upload-section">
        <label htmlFor="file-upload" className="upload-label" onDrop={handleDrop} onDragOver={e => e.preventDefault()}>
          {image ? (
            <img src={image} alt="Uploaded" className="preview-img" />
          ) : (
            <div className="upload-placeholder">
              <span role="img" aria-label="camera" className="camera-icon">üì∑</span>
              <div>‡¶â‡¶™‡¶∞‡ßá‡¶∞ ‡¶®‡ßÄ‡¶≤ ‡¶¨‡¶æ‡¶ü‡¶®‡ßá ‡¶ï‡ßç‡¶≤‡¶ø‡¶ï ‡¶ï‡¶∞‡ßá ‡¶Ö‡¶•‡¶¨‡¶æ ‡¶è‡¶ñ‡¶æ‡¶®‡ßá ‡¶õ‡¶¨‡¶ø ‡¶è‡¶®‡ßá ‡¶õ‡ßá‡ßú‡ßá ‡¶¶‡¶ø‡¶®</div>
              <div className="formats">JPG, PNG, GIF, BMP, WebP ‡¶´‡¶∞‡¶Æ‡ßç‡¶Ø‡¶æ‡¶ü‡ßá‡¶∞ ‡¶õ‡¶¨‡¶ø‡¶ó‡ßÅ‡¶≤‡¶æ ‡¶∏‡¶æ‡¶™‡ßã‡¶∞‡ßç‡¶ü ‡¶ï‡¶∞‡¶¨‡ßá</div>
            </div>
          )}
          <input
            id="file-upload"
            type="file"
            accept="image/jpeg,image/png,image/gif,image/bmp,image/webp"
            onChange={handleImageChange}
            ref={fileInput}
            style={{ display: "none" }}
          />
        </label>
        <div className="button-row">
