    ctx.lineTo(BOX_X + BOX_WIDTH - BOX_RADIUS, BOX_Y);
    ctx.quadraticCurveTo(BOX_X + BOX_WIDTH, BOX_Y, BOX_X + BOX_WIDTH, BOX_Y + BOX_RADIUS);
