    ctx.quadraticCurveTo(BOX_X, BOX_Y, BOX_X + BOX_RADIUS, BOX_Y);
    ctx.closePath();
    ctx.clip();
    ctx.drawImage(userImg, drawX, drawY, drawW, drawH);
    ctx.restore();

